mapper.select:
SELECT *
FROM product
WHERE product.created_at::date >= #{dayFrom}
AND product.created_at::date <= #{dayTo}
ORDER BY product.product_id

mapper.insert:
INSERT 
INTO product(
	product_id
	, product_name
	, product_type
	, price
	, image_url
	, description
)
VALUES(
	#{productId}
	, #{productName}
	, #{productType}
	, #{price}
	, #{imageUrl}
	, #{description}
)

mapper.select:

SELECT *
FROM product
WHERE product_name LIKE #{bindedKeyWord}
OR description LIKE #{bindedKeyWord}
ORDER BY product_id

mapper.update:
UPDATE product
SET product_name = #{productName}
	, product_type = #{productType}
	, price = #{price}
	, description = #{description}
	, updated_at = now()
WHERE product_id = #{productId}

UPDATE product
SET image_url = #{imageUrl}
WHERE product_id = #{productId}
